class Solution:
    def pushDominoes(self, dominoes: str) -> str:
        dominoes = list(dominoes)
        slow, fast = 0, 0
        while fast < len(dominoes):
            if dominoes[fast] == "R":
                # R...L
                # R.R.R
                # RR...
                # "R...."
                slow = fast
                fast += 1
                while fast < len(dominoes) and dominoes[fast] == ".":
                    fast += 1
                if (
                    fast == len(dominoes)  # end of string
                    or fast < len(dominoes)
                    and dominoes[fast] == "R"
                ):
                    # R.... fast == len(dominoes)
                    # R...R dominoes[fast] == "R"
                    # RR... dominoes[fast] == "R"
                    dominoes[slow:fast] = ["R"] * (fast - slow)
                    slow = fast
                # else:
                # R...L will be addressed in the next iteration
            elif dominoes[fast] == "L":
                if dominoes[slow] == "R":
                    # if (fast - slow) % 2 == 0
                    # .R...L.
                    # 0123456
                    # 5 - 1 = 4
                    # there will be a middle domino
                    # otherwise
                    # .R..L.
                    # 012345
                    # 4 - 1 = 3
                    # so there will be no middle domino
                    middle = int((fast - slow) % 2 == 0)
                    dominoes[slow + 1 : fast] = (
                        ["R"] * ((fast - slow) // 2 - middle)
                        + ["."] * middle
                        + ["L"] * ((fast - slow) // 2 - middle)
                    )
                    slow = fast + 1
                    fast += 1
                else:
                    # L...L
                    # ....L
                    dominoes[slow:fast] = ["L"] * (fast - slow)
                    slow = fast + 1
                    fast += 1
