class Solution {

    public int prims(HashMap<point, List<point>> adjList, HashSet<point> visited, PriorityQueue<edge> minHeap,
            int sum) {
        edge top = minHeap.poll();
        if (top == null) {
            return sum;
        }
        while (visited.contains(top.p2)) {
            top = minHeap.poll();
            if (top == null) {
                return sum;
            }
        }
        sum += top.weight;
        point start = top.p2;
        visited.add(start);
        List<point> neighbours = adjList.get(start);
        neighbours.forEach((neighbour) -> {
            if (!visited.contains(neighbour)) {
                int weight = Math.abs(start.x - neighbour.x) + Math.abs(start.y - neighbour.y);
                minHeap.add(new edge(start, neighbour, weight));
            }
        });

        return prims(adjList, visited, minHeap, sum);

    }

    public int minCostConnectPoints(int[][] points) {
        if (points.length == 1) {
            return 0;
        }
